const testFns = {
    a: x => x * 2,
    b: x => x + 10
  }plan(testFns, { a: 5, b: 10 }) // => { a: 10, b: 20 }// It's also curriedconst p = plan(testFns)p({ a: 5, b: 10 }) // => { a: 10, b: 20 }
