reduce((acc, n) => acc + n, 0, [1, 2, 3, 4, 5]) // => 15reduce((acc, n) => {
    if (typeof n === 'number') {
      acc.push(n)
    }

    return acc
  }, [], ['', 1, 2, '0', 3]) // => [1, 2, 3]

const testFns = {
    a: x => x * 2,
    b: x => x + 10
  }plan(testFns, { a: 5, b: 10 }) // => { a: 10, b: 20 }// It's also curriedconst p = plan(testFns)p({ a: 5, b: 10 }) // => { a: 10, b: 20 }
